var task_lists_api = require('../../google_api/task_lists_api.js')
var tasks_db = require('../../db_layer/tasks_db.js')

module.exports.fetchTasks = function (req, res, next) {
	task_lists_api.fetchTasks(req, res, next);
};

module.exports.getLists = function (req, res, next) {
	tasks_db.getLists(req, res, next);
};

module.exports.saveLists = function(req, res, next) {
	tasks_db.saveLists(req, res, next);
}

module.exports.get = function (req, res, next) {
	console.log("req.tasks:");
	console.log(req.tasks);
	res.render('tasks', {
		title: 'Task Lists',
      	tasks: req.tasks || []
	});	
};
